import {useEffect, useState} from "react";
import axios from "axios";
import './dssp.css'
export default function Dssp() {
    const [products, setProducts] = useState([]);
    const [categories, setCategories] = useState([]);
    const [selectcategories, setSelectCategories] = useState(null);
    const [newProduct, setNewProduct] = useState({title:'',category:'',price:0});
    const [selectProduct,setSelectProduct] = useState(null);
    function loadProducts(){
        axios.get('http://localhost:9999/products').then((response) => {
            let list = response.data;
            const filterList = [...new Set(list.map(item => item.category))];
            setCategories(filterList)
            setProducts(list);
        })
    }

    function filterCate(products){
        if(selectcategories ===null) return products
        return products.filter(product => product.category === selectcategories)
    }

    function addProduct(){
        axios.post('http://localhost:9999/products',newProduct).
        then((x) => {loadProducts();
            setNewProduct({title:'',category:'',price:0});
        })
    }

    function handChangeNewProduct(e){
        setNewProduct({...newProduct, [e.target.name]: e.target.value});
    }

    useEffect (() => {loadProducts()}, [])
    return (
        <>
            <div className={'container'}>
                <div className={'button'}>
                    <button onClick={() =>
                        setSelectCategories(null)}>tất cả</button>
                    {categories.map(category => (
                    <div>
                        <button onClick={() =>
                            setSelectCategories(category)}>{category}</button>
                    </div>
                    ))}
                </div>
                <div className={'products'}>
                    {filterCate(products).map((product) => (
                       <div className={'list-product'}>
                           <span>{product.title}</span>
                           <div>{product.category}</div>
                           <div>{product.price}</div>
                           <button onClick={() => setSelectProduct(product) }>chitiet</button>
                       </div>

                    ))}
                </div>

                <div className={'review-edit'}>
                    <input type="text" value={newProduct.title} name={'title'} onChange={handChangeNewProduct}/>
                    <input type="text" value={newProduct.category} name={'category'} onChange={handChangeNewProduct}/>
                    <input type="text" value={newProduct.price} name={'price'} onChange={handChangeNewProduct}/>
                    <button onClick={() => addProduct()}>thêm mới</button>
                </div>
                <div className={'show'}>
                    {selectProduct && (
                        <>
                            <h3> Name: {selectProduct.title}</h3>
                            {selectProduct.review && selectProduct.review.map((review => (
                             <div> comment: {review.comment}, rate:{review.rating}</div>
                            )))}
                        </>
                        )}
                </div>
        </div>
        </>
    )
}
