import {useEffect, useState} from "react";
import axios from "axios";
import './student.css'

export default function QuanLiHocSinh (){
    const [hocSinh, setHocSinh] = useState([]);
    const [danhSachLop, setDanhSachLop] = useState('');
    useEffect(()=>{loadData()},[])
    function loadData(){
        axios.get('http://localhost:3456/students').then(response => {
            let list = response.data
            let filterList = [...new Set(list.map(item => item.class))];
            setDanhSachLop(filterList)
            setHocSinh(list)
        })
    }
    function filterHS(hocSinh){
        if(!danhSachLop)return hocSinh
        return hocSinh.filter(student => student.class === setDanhSachLop)
    }

    // json-server --watch duLieu.json --port 3456
    return(
        <>
            <div className={'container1'}>
                <select name="" id=""></select>
                <div className={'table'}>
                    <select value={danhSachLop.class} onChange={(e) =>{setDanhSachLop(e.target.value)}}>
                        {danhSachLop.map((item,index) =>{
                        <option value={item} key={index}></option>})}
                    </select>
                    {filterHS(hocSinh).map((student) => (
                        <div className={'list'}>
                        <div className={'name'}>tên: {student.name}</div>
                        <div className={'class'}>lớp: {student.class}</div>
                        <div className={'gpa'}>Điểm Trung Bình: {student.gpa}</div>
                        </div>
                    ))}
                </div>
            </div>
        </>
    )
}
