import {useEffect,useState} from "react";
import axios from "axios";
export default function ListProduct(){
    const [products, setProducts] = useState( [])
    let [categories, setCategories] = useState([]);
    let [categoriesSelect, setCategoriesSelect] = useState(null);
    function loadProduct(){
        axiox.get("http://localhost:9999/products").then(response => {
            let list = response.data;
            let listCategories = []
            for(let i=0; i<list.length; i++){
                if(listCategories.indexOf(list[i].category) === -1){
                    listCategories.push(list[i].category);
                }
            }
            setCategories(listCategories);
            setProducts(response.data);
        })
    }

    function FilterProduct(products){
    if(categoriesSelect === null) return products;
    return products.filter((product) => product.category === categoriesSelect)
    }

    useEffect(() => {
        loadProduct();
    }, [] )
    return (
        <>
            <h1>List Product</h1>
            {categories.map((category) => (
                <button onClick = {() => {setCategoriesSelect(category)}}>
                    {category}
                </button>))}
            {FilterProduct(products).map((product,) => (
                <h2>{product.title}: {product.category}, ${product.price} </h2>
            ))}

        </>

    )
}
