import {useEffect, useState} from "react";
import axios from "axios";
import './dssp.css'
export default function Dssp(){
    const [products, setProducts] = useState([]);
    let [categories, setCategories] = useState('');
    let [categorySelect, setCategorSelect] = useState('');
    function loadProduct (){
        axios.get("http://localhost:9999/products").then((response) =>
        {const list = response.data;
            setProducts(list);
            const listCategories = [...new Set(list.map(item =>
                item.category))];
            setCategories(listCategories);
        })
    }
    //  const listCategories = [...new Set(list.map(item => item.category))];
    //
    useEffect(()=>{loadProduct ()},[])
    return (
        <>
            <div className={'containner'}>

                <div className={'product'}>
                    {categories.map((category,) => (<button
                        onClick={() => {setCategorSelect(category)}}>
                    </button>))}

                    <div className={'input'}>Ã´ input </div>
                    <div className={'list-product'}>
                        {products.map((product, ) => (
                            <div className={'item'} >
                                <span>{product.title}</span>
                                <div>{product.category}</div>
                                <div>{product.price}</div>
                            </div>
                        ))}
                    </div>


                </div>
                <div className={'edit-review'}></div>
            </div>
        </>
    )
}
