import {useEffect, useState} from "react";
import axios from "axios";
import './dssp.css'
export default function Dssp(){
    let [products, setProducts] = useState([]);
    let [categories, setCategories] = useState([]);
    let [selectCategories, setSelectCategories] = useState(null);

    function loadProduct(){
        axios.get('http://localhost:9999/products').then((response) =>
        {    let list = response.data
            let filterCate= [...new Set(list.map((item) => item.category))];
            setCategories(filterCate)
            setProducts(list);
        })
    }

    function filterCaterory(products){
        if(selectCategories === null) return products
        return products.filter((product) => product.category === selectCategories)
    }

    useEffect(() => {loadProduct()}, [])
    return (
        <>
            <div className={'container'}>
                <button onClick={() => setSelectCategories(category)} >{category}</button>
                <div className={'input'}></div>
                <div className={'products'}>
                    {filterCaterory(products).map((product) => (
                        <div className={'list-product'}>
                            <span>{product.title}</span>
                            <div>{product.category}</div>
                            <div>{product.price}</div>
                        </div>
                    ))}
                </div>
                <div className={'edit-review'}></div>
            </div>
        </>
    )
}
