import {useEffect, useState} from "react";
import axios from "axios";
import './student.css'

export default function QuanLiHocSinh (){
    const [students, setStudents] = useState([]);
    function loadData(){
        axios.get('http://localhost:3456/students').then(response => {
            let list = response.data
            setStudents(list)
        })
    }
   useEffect(()=>{loadData()},[])


    return(
        <>
            <div className={'container'}>
                <div className={'table'}>
                    {students.map((student) => (
                        <div className={'list'}>
                        <div className={'name'}>tên: {student.name}</div>
                        <div className={'class'}>lớp: {student.class}</div>
                        <div className={'gpa'}>Điểm Trung Bình: {student.gpa}</div>
                        </div>
                    ))}
                </div>
            </div>
        </>
    )
}
