import {useEffect,useState} from "react";
import axios from "axios";
export default function ListProduct(){
    const [products, setProducts] = useState( [])
    let [categories, setCategories] = useState([]);
    let [categoriesSelect, setCategoriesSelect] = useState(null);
    const [showDetailId, setShowDetailId] = useState(null);
    let [newProduct, setNewProduct] = useState({title: '', category:'', price: 0});
    function loadProduct(){
        axios.get("http://localhost:9999/products").then(response => {
            let list = response.data;
            let listCategories = []
            for(let i=0; i<list.length; i++){
                if(listCategories.indexOf(list[i].category) === -1){
                    listCategories.push(list[i].category);
                }
            }
            setCategories(listCategories);
            setProducts(response.data);
        })
    }

    function FilterProduct(products){
    if(categoriesSelect === null) return products;
    return products.filter((product) => product.category === categoriesSelect)
    }
    function toggleDetail(id) {
        if (showDetailId === id) {
            setShowDetailId(null); // đang mở rồi, thì đóng lại
        } else {
            setShowDetailId(id);   // đang đóng, thì mở sản phẩm mới
        }
    }

    function addProduct(){
        axios.post("http://localhost:9999/products",newProduct ).then(x =>
        {loadProduct();setNewProduct({title: '', category:'', price: 0})
        })
    }

    function handleChangeNewProduct(e) {
        setNewProduct({...newProduct, [e.target.name]: e.target.value});
    }

    useEffect(() => {
        loadProduct();}, [] )
    return (
        <>
            <h1>List Product</h1>
            <button onClick={() => setCategoriesSelect(null)}>Tất cả</button>
            {categories.map((category) => (
                <button onClick = {() => {setCategoriesSelect(category)}}>
                    {category}
                </button>))}
            {FilterProduct(products).map((product,) => (
                <h2>{product.title}: {product.category}, ${product.price} <button
                    onClick={() => toggleDetail(product.id)}>chi tiêt
                </button>
                    {showDetailId === product.id && (
                        <div>
                            <p>Đánh giá:</p>
                            {(() => {
                                if (product.reviews && product.reviews.length > 0) {
                                    return (
                                        <ul>
                                            {product.reviews.map((r, i) => (
                                                <p key={i}>- {r.comment}</p>
                                            ))}
                                        </ul>
                                    );
                                } else {
                                    return <p>Chưa có đánh giá</p>;
                                }
                            })()}
                        </div>
                    )}
                    <input type="text" placeholder={'nhập sản phẩm'} value={newProduct.title} onChange={handleChangeNewProduct} />
                    <input type="text" placeholder={'nhập sản phẩm'} value={newProduct.category} onChange={handleChangeNewProduct}/>
                    <input type="text" placeholder={'nhập sản phẩm'} value={newProduct.price} onChange={handleChangeNewProduct}/>
                    <button onClick={() => addProduct()}>thêm mới</button>
                </h2>
            ))}

        </>

    )
}
